mapper.select:
select r.id,if(CHAR_LENGTH(v.purpose)  <= 8,v.purpose,concat(left(v.purpose,5),'..')) as purpose, v.name,v.employee_name,v.visit_date1,v.visit_date2,v.phone_number,v.withPerson,
if(CHAR_LENGTH(v.company)  <= 8,v.company,concat(left(v.company,5),'..')) as company,v.is_checked from Reservation_info r inner join visitor v on r.visitor_id = v.id



        and r.employee_id = #{employee_id}


        and v.name like concat('%',#{name},'%')


        and date(v.visit_date1) between #{date1} and #{date2}


        and date(v.visit_date1) >= #{date1}



        and date(v.visit_date2)  <=  #{date2}


        and v.is_checked like #{is_checked}



order by v.visit_date1 asc ,r.id asc

select r.id,if(CHAR_LENGTH(v.purpose)  <= 8,v.purpose,concat(left(v.purpose,5),'..')) as purpose, v.name,v.employee_name,v.visit_date1,v.visit_date2,v.phone_number,v.withPerson,
if(CHAR_LENGTH(v.company)  <= 5,v.company,concat(left(v.company,3),'..')) as company,v.is_checked from Reservation_info r inner join visitor v on r.visitor_id = v.id


        and v.name like concat('%',#{name},'%')


        and date(v.visit_date1) between #{date1} and #{date2}


        and date(v.visit_date1) >= #{date1}



        and date(v.visit_date2)  <=  #{date2}


        and v.is_checked like #{is_checked}


order by v.write_date desc

select r.id,if(CHAR_LENGTH(v.purpose)  <= 8,v.purpose,concat(left(v.purpose,5),'..')) as purpose, v.name,v.employee_name,v.visit_date1,v.visit_date2,v.phone_number,v.withPerson,
if(CHAR_LENGTH(v.company)  <= 5,v.company,concat(left(v.company,3),'..')) as company,v.is_checked from Reservation_info r inner join visitor v on r.visitor_id = v.id


        and v.name like concat('%',#{name},'%')



        and date(v.visit_date1) like #{date1}


        and v.is_checked like #{is_checked}


order by v.visit_date1 asc

select r.id,v.purpose, v.name,v.employee_name,v.visit_date1,v.visit_date2,v.phone_number,v.withPerson,v.company,v.is_checked from Reservation_info r inner join visitor v on r.visitor_id = v.id

    v.visit_date1 between #{date1} AND #{date2}

        and v.is_checked like #{is_checked}


order by r.id desc
